if (!$json.results.albums) { return { error: "Nicht bei Apple Music verfügbar" }};

const album = $json.results.albums.data[0];

return {
  album: album.attributes.name,
  artist: album.attributes.artistName,
  appleMusicLink: album.attributes.url,
  albumId: album.id,
  coverUrl: album.attributes.artwork.url
};

const aiContentItems = $input.all();
const completeContentItems = $("Merge content & apple link").all(); 

for (let i = 0; i < aiContentItems.length; i++) {
  const item = aiContentItems[i];
  const source = completeContentItems[i]; // greift auf das i-te Item im "If" Node zu

  if (!source) continue; // falls weniger Items im "If"-Node vorhanden sind, überspringen

  item.json.album = source.json.album;
  item.json.artist = source.json.artist;
  item.json.content = item.json.output;
  item.json.appleMusicLink = source.json.appleMusicLink || ''
  item.json.link = source.json.link
  item.json.coverUrl = source.json.coverUrl
  item.json.rating = source.json.rating
  item.json.lastmod = source.json.lastmod

  delete item.json.output;
}

return aiContentItems;

