const items = $input.all();

const emailText = items.map(item => {
  const { artist, album, content, appleMusicLink, link, coverUrl, rating } = item.json;
  let contentHtml = content.replace(/^##(.*)$/gm, '<h3>$1</h3>');
  contentHtml = contentHtml.replaceAll('\n', '<br>');
  contentHtml = contentHtml.replaceAll('</h3><br>', '</h3>');
  let largeCoverUrl = coverUrl.replaceAll('{w}x{h}', '400x400');

let magazin = "";

if (link.includes("clashmusic")) {
  magazin = `<a href="${link}" class="clashlogo" alt="Clash Music"></a>`;
} else if (link.includes("ra.co")) {
  magazin = `<a href="${link}" class="ralogo" alt="Resident Advisor"></a>`;
} else if (link.includes("musikexpress")) {
  magazin = `<a href="${link}" class="musikexpresslogo" alt="MusikExpress"></a>`;
} else {
  magazin = `<a href="${link}" class="lautlogo" alt="Laut Musikmagazin"></a>`;
}

  return (
    `<article><h2>${artist} – ${album}</h2>` +
    `<img src="${largeCoverUrl}" width="200" height="auto" />` +
    `${contentHtml}` + 
    `<h3>${rating}</h3><br>` + 
    `<a href="${appleMusicLink}" class="applemusic"></a>` +
    `${magazin}` + 
    `</article>`
  );
}).join("");

return [{
  json: {
    emailBody: emailText
  }
}];

