const data = $json.data;
var meter = "";

// Clash
const regexClash = /<p>\s*<strong>\s*((?:10|[1-9])\/10)\s*<\/strong>\s*<\/p>/is;
var match = data.match(regexClash);
var rating = String(match ? match[1] : "-")

// Laut
if (match == null) {
  const regexLaut = /<meta\s+(?:[^>]*?\s)?itemprop=["']rating["'][^>]*?\scontent=["']([^"']+)["']/i;
  match = data.match(regexLaut);
  rating = String(match ? (Number(match[1]) * 2) + "/10" : "-") 
}

// MusikExpress
if (match == null) {
  const regexMusikExpress = /class=["']asmb-rating[^"']*["'][^>]*style=["'][^"']*width:\s*([\d.]+)%/i;
  match = data.match(regexMusikExpress);
  rating = String(match ? (Math.round(parseFloat(match[1])/100 * 10)) + "/10" : "-")
}

return {
  data: data,
  rating: String(rating)
};

// Eingabe: z. B. über vorherigen HTTP Request Node
// const html = $json["html"]; // wenn HTML aus vorherigem Node kommt
const html = $json.data;

// 1. Alle <script type="application/ld+json"> Blöcke finden
const scriptRegex = /<script[^>]*type=["']application\/ld\+json["'][^>]*>([\s\S]*?)<\/script>/gi;
const matches = [...html.matchAll(scriptRegex)];

// Wenn keine Treffer gefunden → gib einfach den HTML-Inhalt zurück
if (matches.length === 0) {
  return {
    "data": html
  };
}

let reviewData = null;

// 2. Jeden gefundenen Block prüfen, ob er "@type": "Review" enthält
for (const match of matches) {
  const scriptContent = match[1].trim();

  try {
    const json = JSON.parse(scriptContent);

    // Manche Seiten haben ein Array oder verschachtelte Strukturen
    const data = Array.isArray(json) ? json : [json];

    for (const obj of data) {
      if (obj['@type'] === 'Review') {
        reviewData = obj;
        break;
      }
    }

    if (reviewData) break;
  } catch (e) {
    // Falls JSON fehlerhaft formatiert ist, einfach überspringen
    continue;
  }
}

// 3. Werte extrahieren
let name = null;
let abstract = null;
let reviewBody = null;

if (reviewData) {
  name = reviewData.name || null;
  abstract = reviewData.abstract || null;
  reviewBody = reviewData.reviewBody || null;
} else {
  return {"data": html}
}

// 4. Rückgabe
return {"data": `<html><body><h2 class="ratitle">${name}</h2><article class="racontent"><p>${abstract}</p><p>${reviewBody}</p></article></body></html>` };

const rows = $input.all().map(i => {
  const link = i.json.link.replace(/'/g, "''");
  const lastmod = i.json.lastmod.replace(/'/g, "''");
  return `SELECT '${link}' AS link, '${lastmod}' AS lastmod`;
});

const unions = rows.join('\nUNION ALL\n');

const query = `
SELECT tmp.link, tmp.lastmod
FROM (
  ${unions}
) AS tmp
WHERE NOT EXISTS (SELECT 1 FROM review_reviews AS a
  WHERE a.link = tmp.link);
`;

return [{ json: { query } }];

