  .map(i => i.json)
  // 1️⃣ Nur URLs, die "/reviews/" enthalten
  .filter(entry => entry.loc?.includes('/reviews/'))
  // 2️⃣ lastmod innerhalb der letzten 60 Tage
  .filter(entry => {
