    cutoff.setDate(cutoff.getDate() - 60);
    return lastmodDate >= cutoff;
  })
  // 3️⃣ Felder mappen
  .map(entry => ({
    link: entry.loc,
    lastmod: entry.lastmod + "T00:47:11.000Z"
  }));

return urls.map(obj => ({ json: obj }));

